mapper.insert:
INSERT INTO tasks(
	task_content,
	task_note,
	task_deadline
	)
VALUES(
	#{taskContent},
	#{taskNote},
	#{taskDeadline}
	)

mapper.update:
UPDATE tasks
SET completed_flag = true
WHERE task_id = #{taskId}

UPDATE tasks
SET task_content = #{taskContent},
	task_note = #{taskNote},
	task_deadline = #{taskDeadline}
WHERE task_id = #{taskId}
